  }

  return (
    <div style={{ color: 'red' }}>
      {notOk.map((item, i) => (
        <div key={i}>‚ùå {item}</div>
      ))}
      <div>‚úÖ Other All OK</div>
    </div>
  );
};


  return (
    <div className="entries-card-list">
      <h2 style={{ textAlign: 'center' }}>üìã ‡§∏‡§≠‡•Ä ‡§ö‡•á‡§ï‡§∂‡•Ä‡§ü ‡§è‡§Ç‡§ü‡•ç‡§∞‡•Ä</h2>
